// Font Family
$font-family: "Noto Sans JP", sans-serif;

// h1
@mixin display {
  font-size: 48px;
  line-height: auto;
  font-weight: 500;
}

// h2
@mixin h2 {
  font-size: 34px;
  line-height: 32px;
  font-weight: 500;
  margin-bottom: 4rem;
}

// h3
@mixin h3 {
  font-size: 26px;
  line-height: 32px;
  font-weight: 400;
}

// h4
@mixin h4 {
  font-size: 20px;
  line-height: 24px;
  font-weight: 400;
}

// h5
@mixin h5 {
  font-size: 16px;
  line-height: 24px;
  font-weight: 500;
}
// body
@mixin body {
  font-size: 16px;
  font-weight: 400;
}

// caption
@mixin caption {
  font-size: 12px;
  line-height: 16px;
  font-weight: 400;
}

